export const dateTimeStyle = () => {
  return {
    background: "#344e41",
    padding: "10px",
    textAlign: "center",
    zIndex: "9999",
    width: "fit-content",
    borderRadius: "5px",
  };
};

export const closeIconStyle = () => {
  return {
    cursor: "pointer",
    // float: "right",
    position: "absolute",
    top: "0",
    right: "0",
    border: "1px solid red",
  };
};

export const pinnedMesssageStyle = () => {
  return {
    background: "rgba(20,20,20,0.1)",
    width: "fit-content",
    height: "auto",
    position: "relative",
    top: "0px",
    left: "0px",
    borderRadius: "5px",
  };
};

export const pinnedMessageTextStyle = () => {
  return {
    font: "600 13px Inter,sans-serif",
    width: "100%",
    minHeight: "25px",
    padding: "10px 20px 0px 5px",
  };
};
