import React from "react";

import {
  CometChatMessageList,
  CometChatMessages,
  CometChatMessageTemplate,
} from "react-ui-kit-testing";

import iconURL from "./resources/empty.svg";

import { Hook } from "./hook";

import { gifBoardStyle } from "./style";
import { toBeInTheDOM } from "@testing-library/jest-dom/dist/matchers";

const GifycatBoard = (props) => {
  const [_user, setUser] = React.useState(null);
  const [_group, setGroup] = React.useState(null);
  const [gif, setGif] = React.useState(null);
  //console.log("gif", gif?.gfycats);

  const sendGifMessage = (e) => {
    console.log(e);
  };

  let gifBoardList = gif?.gfycats?.map((items) => {
    return (
      <div
        className="gif--items"
        style={{ margin: "10px 3px", cursor: "pointer" }}
      >
        <img
          key={items?.gfyId}
          src={items?.gifUrl}
          alt="Girl in a jacket"
          width="70"
          height="70"
          onClick={sendGifMessage.bind(items)}
        />
      </div>
    );
  });

  let messageListConfig = {};
  if (props?.messageTypes) {
    messageListConfig = {
      messageTypes: props?.messageTypes,
    };
  }
  Hook(setGroup, setUser, setGif);
  return props?.messageTypes || props?.messageTypes?.length ? (
    <>
      <div className="gif--board" style={gifBoardStyle()}>
        {gifBoardList}
      </div>
    </>
  ) : null;
};

export { GifycatBoard };
