export const headerContainerStyle = () => {
  return {
    width: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "row",
    position: "absolute",
    top: "0",
    left: "0",
    border: "1px solid white",
    padding: "20px",
    background: "#fff",
    overflow: "hidden",
  };
};

export const navLinkStyle = () => {
  return {
    minWidth: "60px",
    textDecoration: "none",
    border: "2px solid rgba(255,0,0,0.3)",
    margin: "0 5px",
    padding: "5px",
    textAlign: "center",
    color: "rgba(20,20,20,0.5)",
    borderRadius: "0 0 10px 0",
    font: "600 14px Inter,sans-serif",
    boxShadow: "2px 5px rgba(20,20,20,0.4)",
    cursor: "pointer",
    background: "white",
  };
};
