export const dateTimeStyle = () => {
  return {
    background: "#344e41",
    padding: "10px",
    textAlign: "center",
    zIndex: "9999",
    width: "fit-content",
    borderRadius: "5px",
  };
};

export const closeIconStyle = () => {
  return {
    fontSize: "30px",
    margin: "0",
    cursor: "pointer",
  };
};

export const pinnedMesssageStyle = () => {
  return {
    background: "rgba(20,20,20,0.2)",
    padding: "10px",
    width: "fit-content",
    position: "absolute",
    top: "0",
    left: "0",
    display: "flex",
  };
};

export const pinnedMessageTextStyle = () => {
  return {
    font: "600 13px Inter,sans-serif",
    width: "100%",
    height: "auto",
    margin: "0",
    paddingRight: "20px",
  };
};
