export const alignmentContainerStyle = () => {
  return {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };
};
export const buttonContainerStyle = () => {
  return {
    border: "1px solid black",
    minWidth: "fit-content",
    padding: "10px",
  };
};
export const buttonStyle = () => {
  return {
    margin: "10px",
    padding: "10px 20px",
    border: "none",
  };
};
