import React from "react";
import { CometChat } from "@cometchat-pro/chat";
import { CometChatMessageList } from "@cometchat-pro/react-ui-kit";

const MessageAlignment = (props) => {
  return props.user || props.group ? (
    <CometChatMessageList
      user={props.user}
      group={props.group}
      alignment={"standard"} //['standard','leftAligned']
    />
  ) : null;
};

export { MessageAlignment };
