import React from "react";

import { CometChatMessageList } from "@cometchat-pro/react-ui-kit";
import { Hook } from "./hook";

const CustomMessageBubble = () => {
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);

  const view = <h1>Hello World!</h1>;
  Hook(setGroup, setUser);

  return user || group ? (
    <CometChatMessageList
      user={user}
      group={group}
      alignment={"standard"} //['standard','leftAligned']
      sentMessageInputData={{
        thumbnail: false,
        title: false,
        time: true,
        readReceipt: true,
      }}
      receivedMessageInputData={{
        thumbnail: false,
        title: false,
        time: true,
        readReceipt: false,
      }}
      customView={view}
    />
  ) : null;
};

export { CustomMessageBubble };
