import React from "react";
import { CometChat } from "@cometchat-pro/chat";

const Hook = (setGroup, setUser, setGifList) => {
  React.useEffect(() => {
    /**bitly  */
    CometChat.callExtension("url-shortener-bitly", "POST", "v1/shorten", {
      text: "Your message with URL https://yourdomain.com/very/very/long/url",
    })
      .then((response) => {
        // minifiedText in response
        console.log("bitly resp", response);
      })
      .catch((error) => {
        // Error occured
        console.log("bitly err", error);
      });

    /**giphy */
    const URLgiphy = "v1/trending?offset=1&limit=15";
    CometChat.callExtension("gifs-giphy", "GET", URLgiphy, null)
      .then((response) => {
        // GIFs data from Giphy
        console.log("giphy resp", response);
      })
      .catch((error) => {
        // Error occured
        console.log("giphy resp", error);
      });
    /** gfycats */
    const URLgfycats = "v1/trending?cursor=0";
    CometChat.callExtension("gifs-gfycat", "GET", URLgfycats, null)
      .then((response) => {
        // GIFs data from Gfycat
        //console.log("gif", response);
        setGifList(response);
      })
      .catch((error) => {
        // Error occured
      });
    /**
     * fetching perticular group
     */
    var GUID = "supergroup";
    CometChat.getGroup(GUID).then(
      (group) => {
        setGroup(group);
        console.log("Group details fetched successfully:", group);
      },
      (error) => {
        console.log("Group details fetching failed with exception:", error);
      }
    );

    /**
     * fetching perticualr user user
     */
    let UID = "superhero2";
    CometChat.getUser(UID).then(
      (user) => {
        setUser(user);
        console.log("User details fetched for user:", user);
      },
      (error) => {
        console.log("User details fetching failed with error:", error);
      }
    );
  }, []);
};

export { Hook };
