import React from "react";
import { CometChat } from "@cometchat-pro/chat";

import {
  CometChatMessageList,
  MessageOptionConstants,
  CometChatPopover,
  CometChatMessageEvents,
  CometChatSoundManager,
  CometChatMessageTemplate,
} from "react-ui-kit-testing";

import { dateTimeStyle } from "./style";

import { Hook } from "./hook";

import { getUnixTimestamp, ID } from "../../home/MessageConstant";

import reminderURL from "./resources/reminders.svg";

const ExcludeMessageOptions = () => {
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);
  const [launchReminder, setReminder] = React.useState(false);
  const [time, setTime] = React.useState("");
  const [date, setDate] = React.useState("");
  const [messageObject, setMessageObject] = React.useState({});
  const [remindeList, setReminderList] = React.useState([]);

  React.useEffect(() => {
    CometChat.callExtension("reminders", "GET", "v1/fetch", null)
      .then((response) => {
        // reminders array
        //console.log("list of reminder", response);
        setReminderList(response.reminders);
      })
      .catch((error) => {
        // Some error occured
        console.log("list of reminder", error);
      });
  }, []);

  const playAudio = (customOutgoingMessageSound) => {
    if (customOutgoingMessageSound) {
      CometChatSoundManager.play(
        CometChatSoundManager.Sound.outgoingMessage,
        customOutgoingMessageSound
      );
    } else {
      CometChatSoundManager.play(CometChatSoundManager.Sound.outgoingMessage);
    }
  };

  const messageStatus = Object.freeze({
    inprogress: "inprogress",
    success: "success",
  });

  console.log("message list", remindeList);

  const FetchApi = (e) => {
    setMessageObject(e);
    setReminder(!launchReminder);
  };

  const excludeMessageOptions = [
    // MessageOptionConstants.reactToMessage,
    MessageOptionConstants.editMessage,
    MessageOptionConstants.deleteMessage,
    MessageOptionConstants.copyMessage,
    MessageOptionConstants.translateMessage,
  ];

  const customMessageOptions = [
    // MessageOptionConstants.reactToMessage,
    // MessageOptionConstants.editMessage,
    // MessageOptionConstants.deleteMessage,
    // MessageOptionConstants.copyMessage,
    // MessageOptionConstants.translateMessage,
    {
      iconURL: reminderURL,
      id: "RemindToMessage",
      onClick: FetchApi,
      optionFor: null,
      title: "Reminder",
    },
  ];

  const sentReminder = () => {
    if (date.length && time.length) {
      let reminderDate = date + " " + time;

      let ms = Math.floor(new Date(`${reminderDate}`).getTime());
      console.log(ms);
      setReminder(!launchReminder);
      CometChat.callExtension("reminders", "POST", "v1/reminder", {
        about: parseInt(messageObject.id),
        isCustom: false,
        timeInMS: ms,
      })
        .then((response) => {
          // Reminder created successfully
          // Reminder details with reminderId.
          console.log("reminder resp", response);
        })
        .catch((error) => {
          // Some error occured
          console.log("reminder error", error);
        });
    }
  };

  let reminderView = () => {
    return (
      <>
        <h3
          style={{ background: "red", width: "fit-content", padding: "10px" }}
        >
          Reminder for Message
        </h3>
      </>
    );
  };

  let messageBubbleConfig = {
    customView: reminderView,
  };

  let customView = (
    <div style={dateTimeStyle()}>
      <p style={{ margin: "0" }}>Date</p>
      <input type="date" onChange={(e) => setDate(e.target.value)} />
      <p style={{ margin: "0" }}>Time</p>
      <input type="time" onChange={(e) => setTime(e.target.value)} />
      <button onClick={sentReminder}>Submit</button>
    </div>
  );

  let messageTypes = [];

  let liveStreamCustomTemplate = new CometChatMessageTemplate({
    type: "live stream",
    icon: null,
    name: "reminder",
    category: "custom messages",
    customView: customView,
    onActionClick: null,
  });

  Hook(setGroup, setUser);

  return user || group ? (
    <>
      <CometChatMessageList
        user={user}
        style={{ width: "650px", height: "450px" }}
        alignment={"standard"} //['standard','leftAligned']
        excludeMessageOptions={excludeMessageOptions}
        customMessageOptions={customMessageOptions}
        sentMessageInputData={{
          thumbnail: false,
          title: false,
          time: true,
          readReceipt: true,
        }}
        messageBubbleConfiguration={messageBubbleConfig}
        messageTypes={{}}
      />

      {launchReminder ? (
        <CometChatPopover position="left" x="750" y="350">
          {customView}
        </CometChatPopover>
      ) : null}
    </>
  ) : null;
};

export { ExcludeMessageOptions };
