import React from "react";
import {
  CometChatMessages,
  MessageTypeConstants,
  CometChatMessageTemplate,
  CometChatConversationsWithMessages,
  MessagesConfiguration,
  BaseStyles,
} from "@cometchat-pro/react-ui-kit";

import { Hook } from "./hook";

const ExcludeMessageTypes = () => {
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);

  const _excludeMessageTypes = [
    MessageTypeConstants.text,
    MessageTypeConstants.image,
    MessageTypeConstants.file,
    MessageTypeConstants.poll,
    MessageTypeConstants.sticker,
    MessageTypeConstants.document,
  ];

  const messageConfig = {
    sentMessageInputData: {
      thumbnail: false,
      title: false,
      time: true,
      readReceipt: true,
    },
    receivedMessageInputData: {
      thumbnail: true,
      title: true,
      time: true,
      readReceipt: false,
    },
    excludeMessageType: [
      MessageTypeConstants.text,
      MessageTypeConstants.image,
      MessageTypeConstants.file,
      MessageTypeConstants.poll,
      MessageTypeConstants.sticker,
      MessageTypeConstants.document,
    ],
  };

  let _messagesConfiguration = new MessagesConfiguration({});
  let messageListStyle = _messagesConfiguration.messageListConfiguration;
  let messageListConfiguration = {
    style: new BaseStyles({
      width: "100%",
      height: "400px",
      background: "none",
      border: "none",
      borderRadius: "none",
    }),
  };

  console.log("_messagesConfiguration", _messagesConfiguration);
  let messageTypes = CometChatMessageTemplate.getDefaultTypes();
  let paymentTemplate = new CometChatMessageTemplate({
    type: "payment",
    icon: "assets/credit-card.png",
    name: "Payment",
    // customView: this.customView,
    // actionCallback: this.openPaymentTab,
  });
  messageTypes.push(paymentTemplate);

  const excludeMessageTypes = [
    MessageTypeConstants.image,
    MessageTypeConstants.file,
    MessageTypeConstants.poll,
    MessageTypeConstants.sticker,
    MessageTypeConstants.document,
    //paymentTemplate,
  ];

  const composerConfig = {
    messageTypes: messageTypes,
    //excludeMessageTypes: excludeMessageTypes,
  };
  console.log("messageTypes", messageTypes);
  Hook(setGroup, setUser);
  return user || group ? (
    <CometChatMessages
      user={user}
      group={group}
      alignment={"standard"}
      style={{ width: "100%", height: "100vh" }}
      messageComposerConfiguration={composerConfig}
    />
  ) : null;
};

export { ExcludeMessageTypes };
