export const alignmentContainerStyle = () => {
  return {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  };
};
export const buttonContainerStyle = () => {
  return {
    minWidth: "fit-content",
    padding: "10px",
  };
};
export const buttonStyle = () => {
  return {
    margin: "10px",
    padding: "10px 20px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    background: "rgba(20,20,20,0.08)",
  };
};
