import React from "react";

import {
  CometChatMessageList,
  CometChatMessages,
  CometChatMessageTemplate,
  CometChatMessageEvents,
  CometChatSoundManager,
} from "react-ui-kit-testing";

import { getUnixTimestamp, ID } from "../../home/MessageConstant";

import iconURL from "./resources/empty.svg";
import { useNavigate } from "react-router-dom";
import { CometChat } from "@cometchat-pro/chat";

import { Hook } from "./hook";

import audio from "./resources/sample-incomming.mp3";

import { gifBoardStyle, closeIconStyle } from "./style";
import { toBeInTheDOM } from "@testing-library/jest-dom/dist/matchers";

const GifycatBoard = (props) => {
  const [_user, setUser] = React.useState(null);
  const [_group, setGroup] = React.useState(null);
  const [gif, setGif] = React.useState(null);

  const navigate = useNavigate();

  const messageStatus = Object.freeze({
    inprogress: "inprogress",
    success: "success",
  });

  const playAudio = (customOutgoingMessageSound) => {
    if (customOutgoingMessageSound) {
      CometChatSoundManager.play(
        CometChatSoundManager.Sound.outgoingMessage,
        customOutgoingMessageSound
      );
    } else {
      CometChatSoundManager.play(CometChatSoundManager.Sound.outgoingMessage);
    }
  };

  const sendGifMessage = (e) => {
    if (e) {
      let receiverId = "superhero2";
      let receiverType = "user";
      const customData = {
        src: e.target.src,
      };
      const customType = "gfycats";
      const customMessage = new CometChat.CustomMessage(
        receiverId,
        receiverType,
        customType,
        customData
      );
      customMessage.setSentAt(getUnixTimestamp());
      customMessage.setMuid(String(getUnixTimestamp()));
      CometChatMessageEvents.emit(CometChatMessageEvents.onMessageSent, {
        message: customMessage,
        status: messageStatus.inprogress,
      });

      playAudio(audio);
      CometChat.sendCustomMessage(customMessage)
        .then((message) => {
          const messageObject = { ...message };
          CometChatMessageEvents.emit(CometChatMessageEvents.onMessageSent, {
            message: messageObject,
            status: messageStatus.success,
          });
        })
        .catch((error) => {});
    }
  };

  let gifBoardList = gif?.gfycats?.map((items) => {
    return (
      <div
        className="gif--items"
        style={{ margin: "10px 3px", cursor: "pointer", display: "flex" }}
      >
        <img
          key={items?.gfyId}
          src={items?.gifUrl}
          alt="Girl in a jacket"
          width="70"
          height="70"
          onClick={sendGifMessage.bind(items)}
        />
      </div>
    );
  });

  let messageListConfig = {};
  if (props?.messageTypes) {
    messageListConfig = {
      messageTypes: props?.messageTypes,
    };
  }

  const closegfyBoard = (value) => !value;

  Hook(setGroup, setUser, setGif);
  return props?.messageTypes || props?.messageTypes?.length ? (
    <>
      <div className="gif--board" style={gifBoardStyle()}>
        <span
          onSubmit={closegfyBoard.bind(this)}
          style={closeIconStyle()}
          className="closeIcon"
        >
          &times;
        </span>
        {gifBoardList}
      </div>
    </>
  ) : null;
};

export { GifycatBoard };
