import React from "react";

import {
  CometChatMessageList,
  CometChatMessages,
  CometChatMessageTemplate,
} from "react-ui-kit-testing";

import iconURL from "./resources/empty.svg";

import { Hook } from "./hook";

const CustomMessageTypes = (props) => {
  const [_user, setUser] = React.useState(null);
  const [_group, setGroup] = React.useState(null);

  const myApp = () => {
    console.alert("payment succeeded");
  };

  let messageTypes = CometChatMessageTemplate.getDefaultTypes();

  let paymentTemplate = new CometChatMessageTemplate({
    type: "payment",
    icon: iconURL,
    name: "Payment",
    customView: null,
    onActionClick: myApp,
  });
  messageTypes.push(paymentTemplate);

  let listconfi = {
    messageTypes: props?.messageTypes,
  };

  const customViewTextBubble = (props) => (
    <div className="message-text">{props.messageObject.text}</div>
  );

  const customViewPayment = (props) => (
    <div className="payment--view">
      <div className="payment--title">Payment Successful</div>
      <div className="payment--amount">
        â‚¹ {props?.messageObject?.data?.customData?.amount}
      </div>
    </div>
  );

  Hook(setGroup, setUser);
  return props.messageTypes || props.messageTypes.length ? (
    <CometChatMessages
      user={_user}
      style={{ width: "700px", height: "500px" }}
      messageTypes={props.messageTypes}
      messsageListConfiguration={listconfi}
    />
  ) : null;
};

export { CustomMessageTypes };
