export const dateTimeStyle = () => {
  return {
    background: "#344e41",
    padding: "10px",
    textAlign: "center",
    zIndex: "9999",
    width: "fit-content",
    borderRadius: "5px",
  };
};

export const closeIconStyle = () => {
  return {
    fontSize: "30px",
    margin: "0",
    position: "absolute",
    top: "0px",
    right: "5px",
    cursor: "pointer",
  };
};
