export const gifBoardStyle = () => {
  return {
    width: "400px",
    height: "auto",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    overflow: "auto",
    overflowY: "hidden",
    background: "rgba(20,20,20,0.08)",
  };
};

export const closeIconStyle = () => {
  return {
    float: "right",
    fontSize: "30px",
    margin: "0",
    position: "absolute",
    top: "0px",
    right: "5px",
    cursor: "pointer",
  };
};
