import React from "react";

import {
  CometChatMessages,
  MessagesConfiguration,
  MessageListConfiguration,
  CometChatMessageList,
  CometChatTheme,
  CometChatConversationsWithMessages,
} from "@cometchat-pro/react-ui-kit";

import audio from "./resources/audio.mp3";

import { Hook } from "./hook";

const CustomSoundManager = () => {
  const [_user, setUser] = React.useState(null);
  const [_group, setGroup] = React.useState(null);

  const _theme = new CometChatTheme({});
  const messageListConfig = {
    customIncomingMessageSound: audio,
  };
  const _messagesConfiguration = new MessagesConfiguration(
    messageListConfig || {}
  );
  const composerConfig = {
    customOutgoingMessageSound: audio,
  };
  Hook(setGroup, setUser);

  return _user || _group ? (
    <>
      <CometChatMessages
        user={_user}
        group={_group}
        alignment={"standard"}
        style={{ width: "100%", height: "100vh" }}
        messageComposerConfiguration={composerConfig}
        messageListConfiguration={messageListConfig}
      />
    </>
  ) : null;
};

export { CustomSoundManager };
