import React from "react";
import {
  BrowserRouter as Router,
  Route,
  NavLink,
  Routes,
  useHistory,
} from "react-router-dom";
import { headerContainerStyle, navLinkStyle } from "./style";
import {
  CometChatAvatar,
  CometChatBackdrop,
  CometChatBadgeCount,
  CometChatConversation,
  CometChatConversationList,
  CometChatConversationListItem,
  CometChatConversationsWithMessages,
  CometChatDataItem,
  CometChatGroupList,
  CometChatGroups,
  CometChatGroupsWithMessages,
  CometChatMessageComposer,
  CometChatMessageHeader,
  CometChatMessageList,
  CometChatMessageReceipt,
  CometChatMessages,
  CometChatStatusIndicator,
  CometChatUserList,
  CometChatUsers,
  CometChatUsersWithMessages,
  MessagesConfiguration,
  CometChatTheme,
} from "@cometchat-pro/react-ui-kit";
import { CometChat } from "@cometchat-pro/chat";
import { CustomErrorState } from "../guides/CustomErrorState";
import { CustomLoadingState } from "../guides/CustomLoadingState";
import { CustomEmptyState } from "../guides/CustomEmptyState";

const Home = () => {
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);

  React.useEffect(() => {
    var GUID = "supergroup";
    CometChat.getGroup(GUID).then(
      (group) => {
        setGroup(group);
        console.log("Group details fetched successfully:", group);
      },
      (error) => {
        console.log("Group details fetching failed with exception:", error);
      }
    );

    /**
     * fetching perticualr user user
     */
    let UID = "superhero1";
    CometChat.getUser(UID).then(
      (user) => {
        console.log("User details fetched for user:", user);
      },
      (error) => {
        console.log("User details fetching failed with error:", error);
      }
    );
  }, []);

  const _messagesConfiguration = new MessagesConfiguration({});
  const _theme = new CometChatTheme({});

  console.log(_messagesConfiguration);
  const messageList =
    user || group ? (
      <CometChatMessageList
        user={user}
        group={group}
        alignment={"standard"} //['standard','leftAligned']
      />
    ) : null;
  return (
    <Router>
      <div className="button--links" style={headerContainerStyle()}>
        <NavLink
          title="loading"
          to="/"
          className="nav-link"
          style={navLinkStyle()}
        >
          Loading
        </NavLink>
        <NavLink
          title="empty"
          to="/empty"
          className="nav-link"
          style={navLinkStyle()}
        >
          Empty
        </NavLink>
        <NavLink
          title="error"
          to="/error"
          className="nav-link"
          style={navLinkStyle()}
        >
          Error
        </NavLink>
        <NavLink
          title="sound"
          to="/sound"
          className="nav-link"
          style={navLinkStyle()}
        >
          sound
        </NavLink>
        <NavLink
          title="alignment"
          to="/alignment"
          className="nav-link"
          style={navLinkStyle()}
        >
          alignment
        </NavLink>
      </div>
      <Routes>
        <Route exact path="/" element={<CustomLoadingState />} />
        <Route exact path="/empty" element={<CustomEmptyState />} />
        <Route exact path="/error" element={<CustomErrorState />} />
        <Route exact path="/sound" element={<CometChatMessages />} />
        <Route exact path="/alignment" element={messageList} />
      </Routes>
    </Router>
  );
};

export { Home };
