import React from "react";
import {
  BrowserRouter as Router,
  Route,
  NavLink,
  Routes,
  useHistory,
} from "react-router-dom";
import { headerContainerStyle, navLinkStyle } from "./style";
import {
  MessagesConfiguration,
  CometChatTheme,
} from "@cometchat-pro/react-ui-kit";

import {
  paymentViewStyle,
  paymentTitleStyle,
  paymentAmountStyle,
} from "./style";

import { CometChat } from "@cometchat-pro/chat";
import { CustomErrorState } from "../guides/CustomErrorState";
import { CustomLoadingState } from "../guides/CustomLoadingState";
import { CustomEmptyState } from "../guides/CustomEmptyState";
import { MessageAlignment } from "../guides/MessageAlignment";
import { CustomSoundManager } from "../guides/CustomSoundManager";
import { ExcludeMessageOptions } from "../guides/ExcludeMessageOptions";
import { ExcludeMessageTypes } from "../guides/ExcludeMessageTypes";
import { MessageTheme } from "../guides/MessageTheme";
import { CustomMessageBubble } from "../guides/CustomMessageBubble";

const Home = () => {
  const customViewPayment = (
    <React.Fragment>
      <div className="payment--view" style={paymentViewStyle()}>
        <div className="payment--title" style={paymentTitleStyle()}>
          Sent Successfully
        </div>
        <div className="payment--amount" style={paymentAmountStyle()}>
          ₹ {23}
        </div>
      </div>
    </React.Fragment>
  );

  return (
    <Router>
      <div className="button--links" style={headerContainerStyle()}>
        <NavLink
          title="loading"
          to="/"
          className="nav-link"
          style={navLinkStyle()}
        >
          Loading
        </NavLink>
        <NavLink
          title="empty"
          to="/empty"
          className="nav-link"
          style={navLinkStyle()}
        >
          Empty
        </NavLink>
        <NavLink
          title="error"
          to="/error"
          className="nav-link"
          style={navLinkStyle()}
        >
          Error
        </NavLink>
        <NavLink
          title="sound"
          to="/sound"
          className="nav-link"
          style={navLinkStyle()}
        >
          sound
        </NavLink>
        <NavLink
          title="alignment"
          to="/alignment"
          className="nav-link"
          style={navLinkStyle()}
        >
          alignment
        </NavLink>
        <NavLink
          title="message options"
          to="/options"
          className="nav-link"
          style={navLinkStyle()}
        >
          Message Options
        </NavLink>
        <NavLink
          title="message types"
          to="/types"
          className="nav-link"
          style={navLinkStyle()}
        >
          Message Types
        </NavLink>
        <NavLink
          title="theme"
          to="/theme"
          className="nav-link"
          style={navLinkStyle()}
        >
          Theme
        </NavLink>
        <NavLink
          title="bubble"
          to="/bubble"
          className="nav-link"
          style={navLinkStyle()}
        >
          Bubble
        </NavLink>
      </div>
      <Routes>
        <Route exact path="/" element={customViewPayment} />
        <Route exact path="/empty" element={<CustomEmptyState />} />
        <Route exact path="/error" element={<CustomErrorState />} />
        <Route exact path="/sound" element={<CustomSoundManager />} />
        <Route exact path="/alignment" element={<MessageAlignment />} />
        <Route exact path="/options" element={<ExcludeMessageOptions />} />
        <Route exact path="/types" element={<ExcludeMessageTypes />} />
        <Route exact path="/theme" element={<MessageTheme />} />
        <Route exact path="/bubble" element={<CustomMessageBubble />} />
      </Routes>
    </Router>
  );
};

export { Home };
