import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import App from "./App";
import reportWebVitals from "./reportWebVitals";
import { COMETCHAT_CONSTANTS } from "./CONSTANTS";
import { CometChat } from "@cometchat-pro/chat";

var appID = COMETCHAT_CONSTANTS.APP_ID;
var region = COMETCHAT_CONSTANTS.REGION;

var appSetting = new CometChat.AppSettingsBuilder()
  .subscribePresenceForAllUsers()
  .setRegion(region)
  .build();
CometChat.init(appID, appSetting).then(
  () => {
    let user = "superhero3";
    if (user && user != " ") {
      var UID = user;
      var authKey = COMETCHAT_CONSTANTS.AUTH_KEY;
      CometChat.getLoggedinUser().then((user) => {
        if (!user) {
          CometChat.login(UID, authKey).then((user) => {
            window.location.reload();
            console.log("Login Successful:", { user });
          });
        } else {
        }
      });
    }
    // CometChatLocalize.setLocale("hi");
    // console.log("app is ready to work");
    const root = ReactDOM.createRoot(document.getElementById("root"));
    root.render(
      <React.StrictMode>
        <App />
      </React.StrictMode>
    );
  },

  (error) => {
    console.log("Initialization failed with error:", error);
    // Check the reason for error and take appropriate action.
  }
);
// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
