import React from "react";

import {
  CometChatMessageList,
  CometChatMessageBubble,
  CometChatMessageTemplate,
  CometChatMessages,
  getExtensionsData,
  MetadataConstants,
} from "react-ui-kit-testing";

import { Hook } from "./hook";

import { messageTextStyle, paymentContainerStyle } from "./style";

const CustomMessageBubble = (props) => {
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);
  const [richMediaObject, setLinkPreviewObject] = React.useState();

  const richMediaHandler = (props) => {
    const linkPreviewData = getExtensionsData(
      props?.messageObject,

      MetadataConstants.extensions.linkPreview
    );
    setLinkPreviewObject(linkPreviewData);
  };

  let messageTypes = CometChatMessageTemplate.getDefaultTypes();

  const customViewTextBubble = (props) => {
    richMediaHandler(props);
    return (
      <React.Fragment>
        {!richMediaObject && richMediaObject?.links[0] ? (
          <div className="message-text" style={messageTextStyle()}>
            {props.messageObject.text}
          </div>
        ) : (
          <div className="rich__media">
            <div class="thumbnail">
              <img
                src={richMediaObject.links?.thumbnail[0]?.href}
                alt=""
                height="100px"
                width="100px"
                // *ngIf="richMediaObject.links?.thumbnail"
              />
              <img
                src={richMediaObject.links?.icon[0]?.href}
                alt=""
                height="100px"
                width="100px"
                // *ngIf="!richMediaObject.links?.thumbnail"
              />
            </div>

            <div className="details">
              <div className="title">
                {richMediaObject.meta.site || richMediaObject.meta.title}
              </div>
              <div className="description">
                {richMediaObject.meta.description}
              </div>

              <a href={richMediaObject.meta.canonical} target="_blank">
                View
              </a>
            </div>
          </div>
        )}
      </React.Fragment>
    );
  };

  const customViewPayment = (props) => {
    return (
      <React.Fragment>
        <div className="payment--view" style={paymentContainerStyle()}>
          <div className="payment--title">Payment Successful</div>
          <div className="payment--amount">
            â‚¹ {props?.messageObject?.data?.customData?.amount}
          </div>
        </div>
      </React.Fragment>
    );
  };
  const messagesListConfig = {
    customView: customViewTextBubble,
  };

  Hook(setGroup, setUser);

  return user || group ? (
    <CometChatMessages
      user={user}
      style={{ width: "700px", height: "450px" }}
      // messageTypes={props?.messageTypes}
      messageTypes={[
        new CometChatMessageTemplate({
          type: "text",
          icon: "",
          name: "Text",
          category: "messsages",
          customView: customViewTextBubble,
          // onActionClick: null,
        }),
        new CometChatMessageTemplate({
          type: "payment",
          icon: "",
          name: "Payment",
          category: "custom-message",
          customView: customViewPayment,
          // onActionClick: onActionClickPayment,
        }),
        ...messageTypes,
      ]}
      messageListConfiguration={messagesListConfig}
    />
  ) : null;
};

export { CustomMessageBubble };
