import React from "react";
import {
  BrowserRouter as Router,
  Route,
  NavLink,
  Routes,
} from "react-router-dom";

import { useNavigate } from "react-router-dom";

import {
  MessagesConfiguration,
  CometChatTheme,
  getExtensionsData,
  MetadataConstants,
  CometChatMessageTemplate,
} from "react-ui-kit-testing";

import { getUnixTimestamp, ID } from "./MessageConstant";
import { Hook } from "./hook";

import icon from "./resources/empty.svg";

import {
  headerContainerStyle,
  navLinkStyle,
  paymentViewStyle,
  paymentTitleStyle,
  paymentAmountStyle,
  messagePreviewContainerStyle,
  previewTitleStyle,
  previewImageStyle,
  messageTextStyle,
  paymentContainerStyle,
  paymentHeaderStyle,
  paymentInputStyle,
  paymentButtonStyle,
  closeIconStyle,
  inputFieldStyle,
  sendButtonStyle,
} from "./style";

import { CometChat } from "@cometchat-pro/chat";
import { CustomErrorState } from "../guides/CustomErrorState";
import { CustomLoadingState } from "../guides/CustomLoadingState";
import { CustomEmptyState } from "../guides/CustomEmptyState";
import { MessageAlignment } from "../guides/MessageAlignment";
import { CustomSoundManager } from "../guides/CustomSoundManager";
import { ExcludeMessageOptions } from "../guides/ExcludeMessageOptions";
import { ExcludeMessageTypes } from "../guides/ExcludeMessageTypes";
import { CustomMessageTypes } from "../guides/CustomMessageTypes";
import { MessageTheme } from "../guides/MessageTheme";
import { CustomMessageBubble } from "../guides/CustomMessageBubble";

const Home = () => {
  const [linkPreview, setLinkPreview] = React.useState(null);
  const [linkObject, setLinkObject] = React.useState(null);
  const [input, setInput] = React.useState("");
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);

  let navigate = useNavigate();

  const customViewPayment = (props) => (
    <React.Fragment>
      <div className="payment--view" style={paymentViewStyle()}>
        <div className="payment--title" style={paymentTitleStyle()}>
          Payment Successful
        </div>
        <div className="payment--amount" style={paymentAmountStyle()}>
          â‚¹ {props?.messageObject?.data?.customData?.amount}
        </div>
      </div>
    </React.Fragment>
  );

  const sendPayment = () => {
    let amount = input;
    if (amount) {
      let receiverId = "superhero1";
      let receiverType = "user";
      const customData = {
        amount: 400,
      };
      const customType = "payment";
      const customMessage = new CometChat.CustomMessage(
        receiverId,
        receiverType,
        customType,
        customData
      );
      customMessage.setMetadata({ incrementUnreadCount: true });
      customMessage.setSentAt(getUnixTimestamp());
      customMessage.setMuid(ID());
    }
  };

  const customViewTextBubble = (
    <React.Fragment>
      {!linkPreview ? (
        <div className="message-text" style={messageTextStyle()}>
          text message
        </div>
      ) : (
        <div
          style={messagePreviewContainerStyle()}
          className="message__preview"
        >
          <div style={previewTitleStyle()} className="card__title">
            <span>{linkObject["title"]}</span>
          </div>

          <div style={previewImageStyle()} className="card__image"></div>

          <p className="link--descriptions">description</p>
        </div>
      )}
    </React.Fragment>
  );

  const onActionClickPayment = () => {
    navigate("/payment");
  };

  const closePayment = () => {
    navigate("/types");
  };

  const makePayment = () => {
    console.log("make payment!");
  };

  const paymentMessage = (
    <React.Fragment>
      <div className="payment" style={paymentContainerStyle()}>
        <span
          onClick={closePayment}
          style={closeIconStyle()}
          className="closeIcon"
        >
          &times;
        </span>
        <h1 className="title" style={paymentHeaderStyle()}>
          Payment
        </h1>
        <div className="input--field" style={inputFieldStyle()}>
          <input
            type="input"
            style={paymentInputStyle()}
            className="input--amout"
            onChange={(e) => setInput(parseInt(e.target.value))}
            placeholder="Enter amount..."
          ></input>
        </div>
        <div className="send--button" style={sendButtonStyle()}>
          <button
            type="button"
            style={paymentButtonStyle()}
            onClick={makePayment}
          >
            Send
          </button>
        </div>
      </div>
    </React.Fragment>
  );
  let messageTypes = CometChatMessageTemplate.getDefaultTypes();
  let paymentTemplate = new CometChatMessageTemplate({
    type: "payment",
    icon: icon,
    name: "Payment",
    customView: customViewPayment,
    onActionClick: onActionClickPayment,
  });
  messageTypes.push(paymentTemplate);
  console.log(messageTypes);
  Hook(setGroup, setUser);
  return (
    <div>
      <div className="button--links" style={headerContainerStyle()}>
        <NavLink
          title="loading"
          to="/"
          className="nav-link"
          style={navLinkStyle()}
        >
          Loading
        </NavLink>
        <NavLink
          title="empty"
          to="/empty"
          className="nav-link"
          style={navLinkStyle()}
        >
          Empty
        </NavLink>
        <NavLink
          title="error"
          to="/error"
          className="nav-link"
          style={navLinkStyle()}
        >
          Error
        </NavLink>
        <NavLink
          title="sound"
          to="/sound"
          className="nav-link"
          style={navLinkStyle()}
        >
          sound
        </NavLink>
        <NavLink
          title="alignment"
          to="/alignment"
          className="nav-link"
          style={navLinkStyle()}
        >
          alignment
        </NavLink>
        <NavLink
          title="message options"
          to="/options"
          className="nav-link"
          style={navLinkStyle()}
        >
          Message Options
        </NavLink>
        <NavLink
          title="message types"
          to="/types"
          className="nav-link"
          style={navLinkStyle()}
        >
          Message Types
        </NavLink>
        <NavLink
          title="theme"
          to="/theme"
          className="nav-link"
          style={navLinkStyle()}
        >
          Theme
        </NavLink>
        <NavLink
          title="bubble"
          to="/bubble"
          className="nav-link"
          style={navLinkStyle()}
        >
          Bubble
        </NavLink>
        <NavLink
          title="exclude message types"
          to="/exclude-message-types"
          className="nav-link"
          style={navLinkStyle()}
        >
          Exclude Message Types
        </NavLink>
      </div>
      <Routes>
        {/* <CustomLoadingState /> */}
        <Route exact path="/" element={<CustomLoadingState />} />
        <Route exact path="/empty" element={<CustomEmptyState />} />
        <Route exact path="/error" element={<CustomErrorState />} />
        <Route exact path="/sound" element={<CustomSoundManager />} />
        <Route exact path="/alignment" element={<MessageAlignment />} />
        <Route exact path="/options" element={<ExcludeMessageOptions />} />
        <Route
          exact
          path="/types"
          element={<CustomMessageTypes messageTypes={messageTypes} />}
        />
        <Route exact path="/theme" element={<MessageTheme />} />
        <Route exact path="/bubble" element={<CustomMessageBubble />} />
        <Route
          exact
          path="/exclude-message-types"
          element={<ExcludeMessageTypes />}
        />
        <Route exact path="/payment" element={paymentMessage} />
      </Routes>
    </div>
  );
};

export { Home };
