import React from "react";

import {
  CometChatMessageList,
  CometChatMessages,
  CometChatMessageTemplate,
  CometChatPopover,
  CometChatBackdrop,
} from "react-ui-kit-testing";

import { CometChat } from "react";

import iconURL from "./resources/empty.svg";

import { Hook } from "./hook";
import { GifycatBoard } from "../GifycatBoard";

const CustomMessageTypes = (props) => {
  const [_user, setUser] = React.useState(null);
  const [_group, setGroup] = React.useState(null);
  const [gif, setGif] = React.useState(null);

  let messageListConfig = {};
  if (props?.messageTypes) {
    messageListConfig = {
      messageTypes: props?.messageTypes,
    };
  }

  const popOverStyle = {
    width: "350px",
    height: "auto",
    border: "none",
    background: "#fff",
    borderRadius: "10px",
    zIndex: "999",
    boxShadow:
      "rgba(0, 0, 0, 0.25) 0px 54px 55px, rgba(0, 0, 0, 0.12) 0px -12px 30px, rgba(0, 0, 0, 0.12) 0px 4px 6px, rgba(0, 0, 0, 0.17) 0px 12px 13px, rgba(0, 0, 0, 0.09) 0px -3px 5px",
  };

  Hook(setGroup, setUser, setGif);
  return props?.messageTypes || props?.messageTypes?.length ? (
    <>
      <CometChatMessages
        user={_user}
        style={{ width: "700px", height: "500px" }}
        messageTypes={props?.messageTypes}
        messsageListConfiguration={messageListConfig}
      />
      {props?.launchGfy ? (
        <CometChatPopover position="top" x="100" y="" style={popOverStyle}>
          <GifycatBoard messageTypes={props?.messageTypes} />
        </CometChatPopover>
      ) : null}
    </>
  ) : null;
};

export { CustomMessageTypes };
