import React from "react";
import { CometChat } from "@cometchat-pro/chat";

const Hook = (setGroup, setUser) => {
  React.useEffect(() => {
    CometChat.callExtension("url-shortener-bitly", "POST", "v1/shorten", {
      text: "Your message with URL https://yourdomain.com/very/very/long/url",
    })
      .then((response) => {
        // minifiedText in response
        console.log("responcse", response);
      })
      .catch((error) => {
        // Error occured
        console.log("responce error", error);
      });
  }, []);

  React.useEffect(() => {
    /**
     * fetching perticular group
     */
    var GUID = "supergroup";
    CometChat.getGroup(GUID).then(
      (group) => {
        setGroup(group);
      },
      (error) => {
        console.error(error);
      }
    );

    /**
     * fetching perticualr user user
     */
    let UID = "superhero2";
    CometChat.getUser(UID).then(
      (user) => {
        setUser(user);
      },
      (error) => {
        console.error(error);
      }
    );
  }, []);
};

export { Hook };
