import React from "react";
import { CometChat } from "@cometchat-pro/chat";
import {
  CometChatConversationsWithMessages,
  CometChatTheme,
  Palette,
} from "@cometchat-pro/react-ui-kit";

const MessageTheme = () => {
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);

  React.useEffect(() => {
    /**
     * fetching perticular group
     */
    var GUID = "supergroup";
    CometChat.getGroup(GUID).then(
      (group) => {
        setGroup(group);
        console.log("Group details fetched successfully:", group);
      },
      (error) => {
        console.log("Group details fetching failed with exception:", error);
      }
    );

    /**
     * fetching perticualr user user
     */
    let UID = "superhero2";
    CometChat.getUser(UID).then(
      (user) => {
        setUser(user);
        console.log("User details fetched for user:", user);
      },
      (error) => {
        console.log("User details fetching failed with error:", error);
      }
    );
  }, []);

  const theme = new CometChatTheme({
    palette: new Palette({
      mode: "dark",
      background: {
        light: "#fff",
        dark: "#432818",
      },
      primary: {
        light: "#dda15e",
        dark: "#dda15e",
      },
      accent400: {
        light: "#fff",
        dark: "#432818",
      },
      accent500: {
        light: "#fff",
        dark: "#432818",
      },
      accent600: {
        light: "#fff",
        dark: "#432818",
      },
      accent: {
        light: "#bc6c25",
        dark: "#B6F0D3",
      },
      accent50: {
        light: "#9e2a2b",
        dark: "#141414",
      },
      accent900: {
        light: "ef476f",
        dark: "black",
      },
    }),
  });

  return user || group ? (
    <CometChatConversationsWithMessages
      user={user}
      group={group}
      theme={theme}
    />
  ) : null;
};

export { MessageTheme };
