import React from "react";
import { CometChat } from "@cometchat-pro/chat";

const Hook = (setGroup, setUser, setGif) => {
  React.useEffect(() => {
    const URL = "v1/trending?cursor=0";
    CometChat.callExtension("gifs-gfycat", "GET", URL, null)
      .then((response) => {
        // GIFs data from Gfycat
        //console.log("gif", response);
        setGif(response);
      })
      .catch((error) => {
        // Error occured
      });
  }, []);

  React.useEffect(() => {
    /**
     * fetching perticular group
     */
    var GUID = "supergroup";
    CometChat.getGroup(GUID).then(
      (group) => {
        setGroup(group);
        console.log("Group details fetched successfully:", group);
      },
      (error) => {
        console.log("Group details fetching failed with exception:", error);
      }
    );

    /**
     * fetching perticualr user user
     */
    let UID = "superhero2";
    CometChat.getUser(UID).then(
      (user) => {
        setUser(user);
        console.log("User details fetched for user:", user);
      },
      (error) => {
        console.log("User details fetching failed with error:", error);
      }
    );
  }, []);
};

export { Hook };
