export const gifBoardStyle = () => {
  return {
    maxWidth: "400px",
    height: "auto",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    overflow: "auto",
    overflowY: "hidden",
    background: "rgba(20,20,20,0.08)",
  };
};
