export const customErrorContainerStyle = () => {
  return {
    width: "auto",
    height: "auto",
  };
};

export const errorIconStyle = (icon) => {
  return {
    width: "32px",
    height: "32px",
    WebkitMask: `url(${icon}) center center no-repeat`,
    background: "red",
  };
};
export const errorTextMessageStyle = () => {
  return {
    color: "red",
    font: "700 17px Inter",
    textTransform: "capitalize",
  };
};
