import React from "react";
import { CometChat } from "@cometchat-pro/chat";
import {
  CometChatMessageList,
  MessageTypeConstants,
  MessageOptionConstants,
  CometChatConversationsWithMessages,
} from "@cometchat-pro/react-ui-kit";

const ExcludeMessageTypes = () => {
  const [user, setUser] = React.useState(null);
  const [group, setGroup] = React.useState(null);

  React.useEffect(() => {
    /**
     * fetching perticular group
     */
    var GUID = "supergroup";
    CometChat.getGroup(GUID).then(
      (group) => {
        setGroup(group);
        //console.log("Group details fetched successfully:", group);
      },
      (error) => {
        //console.log("Group details fetching failed with exception:", error);
      }
    );

    /**
     * fetching perticualr user user
     */
    let UID = "superhero2";
    CometChat.getUser(UID).then(
      (user) => {
        setUser(user);
      },
      (error) => {
        console.error(error);
      }
    );
  }, []);

  const _excludeMessageTypes = [
    MessageTypeConstants.image,
    MessageTypeConstants.messageDeleted,
    MessageTypeConstants.text,
  ];
  const messageConfig = {
    sentMessageInputData: {
      thumbnail: false,
      title: false,
      time: true,
      readReceipt: true,
    },
    receivedMessageInputData: {
      thumbnail: true,
      title: true,
      time: true,
      readReceipt: false,
    },
    excludeMessageType: [
      MessageTypeConstants.text,
      MessageTypeConstants.image,
      MessageTypeConstants.file,
      MessageTypeConstants.poll,
      MessageTypeConstants.sticker,
      MessageTypeConstants.document,
    ],
    messageTypes,
  };
  return user || group ? (
    <CometChatConversationsWithMessages
      user={user}
      group={group}
      alignment={"standard"} //['standard','leftAligned']
      messgesConfiguration={messageConfig}
    />
  ) : null;
};

export { ExcludeMessageTypes };
